func unmarshalLFXProjectResponseBodyToSurveyLFXProject(v *LFXProjectResponseBody) *survey.LFXProject {
	if v == nil {
		return nil
	}
	res := &survey.LFXProject{
		ID:      *v.ID,
		Name:    *v.Name,
		Slug:    *v.Slug,
		Status:  *v.Status,
		LogoURL: v.LogoURL,
	}

	return res
}

